import Myheader from "../components/Myheader.vue";
export default {
  data: function data() {
    return {
      center3_dp: "yc",
      upwd2_dp: "yc",
      upwd1_val: "",
      upwd2_val: "",
      upwd2_qx: true,
      upwd: "",
      m_dp: "",
      fm_dp: "yc",
      usex: "",
      uid: "",
      uname: "",
      upic: "",
      uname_val: "",
      btn1_qx: true,
      sex: "",
      userInfo: {
        avatar: 'data:image/jpeg;base64,'
      }
    };
  },
  methods: {
    // 打开图片上传
    uploadHeadImg: function uploadHeadImg() {
      this.$el.querySelector("input.sctpBtn").click();
      this.btn1_qx = false;
    },
    // 将头像显示
    handleFile: function handleFile(e) {
      var _this = this;

      var $target = e.target || e.srcElement;
      var file = $target.files[0];

      if (!file) {
        return;
      }

      var reader = new FileReader();

      reader.onload = function (data) {
        var res = data.target || data.srcElement;
        _this.userInfo.avatar = res.result;
      };

      reader.readAsDataURL(file); //编码  
    },
    btn2Fun: function btn2Fun() {
      this.$router.push("/Login");
      sessionStorage.setItem("uid", "");
    },
    upwd2_valChange: function upwd2_valChange() {
      this.upwd = this.upwd2_val;
      this.btn1_qx = false;
    },
    uname_valChange: function uname_valChange() {
      this.uname = this.uname_val;
      this.btn1_qx = false;
    },
    btn1Fun: function btn1Fun() {
      var _this2 = this;

      var obj = {
        uid: this.uid,
        uname: this.uname,
        upwd: this.upwd,
        usex: this.usex,
        upic: this.userInfo.avatar
      };
      var url = "update";
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        if (res.data.code == 1) {
          _this2.$toast("修改成功");

          _this2.$router.push("/PersonalCenter");
        } else {
          _this2.$toast("修改失败");
        }
      });
    },
    xzFM: function xzFM() {
      this.fm_dp = "xs";
      this.m_dp = "yc";
      this.usex = 0;
      this.sex = "女";
      this.btn1_qx = false;
    },
    xzM: function xzM() {
      this.m_dp = "xs";
      this.fm_dp = "yc";
      this.usex = 1;
      this.sex = "男";
      this.btn1_qx = false;
    },
    cneter3Close: function cneter3Close() {
      this.center3_dp = "yc";
    },
    updateSex: function updateSex() {
      this.center3_dp = "xs";
    },
    updateUpwd: function updateUpwd() {
      this.upwd2_dp = "sx";
      this.$toast("密码为3~16位数字或字母组成");
    }
  },
  watch: {
    upwd1_val: function upwd1_val() {
      var upwd_reg = /^[a-z0-9]{3,16}$/i;
      var upwd = this.upwd1_val;

      if (upwd_reg.test(upwd) == true) {
        this.upwd2_qx = false;
      } else {
        this.upwd2_qx = true;
      }
    }
  },
  created: function created() {
    var _this3 = this;

    if (sessionStorage.getItem("uid") == "null") {
      this.$router.push("/Login");
    } else {
      this.uid = sessionStorage.getItem("uid");
    }

    var url = "szPerCenter";
    var obj = {
      uid: this.uid
    };
    this.axios.get(url, {
      params: obj
    }).then(function (res) {
      var uname = res.data[0].uname;
      var uphone = res.data[0].uphone;
      var upwd = res.data[0].upwd;
      var upic = res.data[0].upic;
      var usex = res.data[0].usex;
      uname == null ? _this3.uname = uphone : _this3.uname = uname;
      upic == null ? upic = _this3.userInfo.avatar : _this3.userInfo.avatar = upic;
      _this3.upwd = upwd;
      _this3.usex = usex;
      usex == 1 ? _this3.sex = "男" : _this3.sex = "女";
    });
  },
  components: {
    "myheader": Myheader
  }
};
